public static int BinarySearch<T>(T[] array, T target) where T : IComparable<T> {\n    return BinarySearch(array, target, 0, array.Length - 1);\n}\n\nprivate static int BinarySearch<T>(T[] array, T target, int left, int right) {\n    if (left > right) return -1;\n    int mid = (left + right) / 2;\n    if (array[mid].CompareTo(target) == 0) return mid;\n    if (array[mid].CompareTo(target) < 0) return BinarySearch(array, target, mid + 1, right);\n    return BinarySearch(array, target, left, mid - 1);\n}