using Outlook = Microsoft.Office.Interop.Outlook;\n\n// assume 'application' is an instance of Outlook.Application\nOutlook.Rules rules = application.Session.DefaultStore.GetRules();\nOutlook.Rule rule = rules.CreateRule();\n\nrule.Name = "My Rule";\nrule.Condition.Subject.Contains("something", RuleCondition.TextMatchCase.Sensitive);\nrule.Actions.MoveToFolder.Folder = application.Session.GetDefaultFolder(Outlook.OlDefaultFolders.olFolderInbox).Folders["xyz"];\n\nrules.Save(true); // save the rule