using System;\nusing System.Diagnostics;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Process p = new Process();\n        p.StartInfo.FileName = "iexplore.exe";\n        p.StartInfo.Arguments = "about:blank";\n        p.Start();\n\n        // Wait for the process to be fully initialized\n        p.WaitForInputIdle();\n\n        // Try to get the MainWindowHandle\n        if (p.HasExited)\n        {\n            Console.WriteLine("Process has exited.");\n        }\n        else\n        {\n            IntPtr mainWindowHandle = p.MainWindowHandle;\n            Console.WriteLine("MainWindowHandle: " + mainWindowHandle);\n        }\n    }\n}