DataTable dt = new DataTable();\n// populate your datatable here...\n\nint NumberOfItemsPerRow = 5;\n\nint rows = (int)Math.Ceiling((double)dt.Rows.Count / NumberOfItemsPerRow);\n\nfor (int i = 0; i < rows; i++)\n{\n    Response.Write("<tr>");\n    for (int j = 0; j < NumberOfItemsPerRow; j++)\n    {\n        int index = i * NumberOfItemsPerRow + j;\n        if (index < dt.Rows.Count)\n        {\n            Response.Write("<td>" + dt.Rows[index]["YourColumnName"] + "</td>");\n        }\n        else\n        {\n            Response.Write("<td>&nbsp;</td>");\n        }\n    }\n    Response.Write("</tr>");\n}