public class ClientDataWebService : System.Web.Services.WebService {\n [WebMethod]\n public ClientDataResponse UpdateClient(XmlDocument clientData) {\n // parse the XML document to extract the fields\n string title = clientData.SelectSingleNode(\"/ClientData/Title\").InnerText;\n string address1 = clientData.SelectSingleNode(\"/ClientData/Address1\").InnerText;\n string address2 = clientData.SelectSingleNode(\"/ClientData/Address2\").InnerText;\n\n // validate the data\n if (title == \"\") {\n return new ClientDataResponse { Status = 1, Message = \"Title is required\"};\n }\n\n // update the data\n // return the status\n return new ClientDataResponse { Status = 0, Message = \"OK\"};\n }\n}