protected void GridView1_RowCommand(object sender, GridViewCommandEventArgs e)\n{\n    if (e.CommandName == "Redirect")\n    {\n        int index = Convert.ToInt32(e.CommandArgument);\n        GridViewRow row = GridView1.Rows[index];\n        string parameter1 = row.Cells[0].Text; // assuming parameter is in first column\n        string parameter2 = row.Cells[1].Text; // assuming parameter is in second column\n\n        // Create a secure URL with parameters\n        string url = "~/AnotherPage.aspx";\n        string queryString = string.Format("param1={0}&param2={1}", HttpUtility.UrlEncode(parameter1), HttpUtility.UrlEncode(parameter2));\n\n        // Use a secure redirect method\n        Response.Redirect(url + "?" + queryString, false);\n    }\n}