using Devart.Data.Oracle;\n\n// Insert a file reference\nOracleCommand cmd = new OracleCommand("INSERT INTO files (id, file_name, file_content) VALUES (:id, :file_name, :file_content)", connection);\ncmd.Parameters.Add("id", 1);\ncmd.Parameters.Add("file_name", "example.pdf");\nOracleBFile file = new OracleBFile("file_dir", "example.pdf");\ncmd.Parameters.Add("file_content", file);\n\n// Retrieve the file contents\nOracleCommand cmd = new OracleCommand("SELECT file_content FROM files WHERE id = :id", connection);\ncmd.Parameters.Add("id", 1);\nOracleBFile file = (OracleBFile)cmd.ExecuteScalar();\nbyte[] fileContents = file.GetContent();