// Authenticate and cache the security context token\nvar client = new ProductServiceClient("wsHttpProductService");\nclient.ClientCredentials.UserName.UserName = "foo";\nclient.ClientCredentials.UserName.Password = "bar";\nclient.Open(); // Authenticate and establish the security context\n\n// Get the security context token\nvar token = client.InnerChannel.Session.SecurityContext.SecurityToken;\n\n// Create a new client instance and reuse the cached token\nvar newClient = new ProductServiceClient("wsHttpProductService");\nnewClient.InnerChannel.Session.SecurityContext.SecurityToken = token;\n\n// Make subsequent calls without re-authenticating\nvar productList = newClient.GetProducts();