// userCreation.ashx\npublic void ProcessRequest(HttpContext context)\n{\n    try\n    {\n        // Process user creation\n        if (/*user creation fails*/) {\n            context.Response.StatusCode = 200;\n            context.Response.ContentType = "application/json";\n            context.Response.Write(JsonConvert.SerializeObject(new { error = "Duplicate username" }));\n            return;\n        }\n        // ...\n    }\n    catch (Exception ex)\n    {\n        // Log the exception\n        context.Response.StatusCode = 200;\n        context.Response.ContentType = "application/json";\n        context.Response.Write(JsonConvert.SerializeObject(new { error = "Internal server error" }));\n        return;\n    }\n}