using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Reflection;\n\nclass AssemblyEncryptor\n{\n    static void Main(string[] args)\n    {\n        // Load the assembly\n        byte[] assemblyBytes = File.ReadAllBytes("MyAssembly.dll");\n\n        // Create an AES instance\n        using (Aes aes = Aes.Create())\n        {\n            // Set the key and IV\n            aes.Key = Convert.FromBase64String("your_secret_key_here");\n            aes.IV = Convert.FromBase64String("your_secret_iv_here");\n\n            // Create an encryptor\n            ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\n\n            // Encrypt the assembly\n            byte[] encryptedBytes = encryptor.TransformFinalBlock(assemblyBytes, 0, assemblyBytes.Length);\n\n            // Save the encrypted assembly\n            File.WriteAllBytes("MyAssembly_encrypted.dll", encryptedBytes);\n        }\n    }\n}