void ShowMessage()\n{\n    // Use a separate thread to display the message box\n    Thread thread = new Thread(() =>\n    {\n        MessageBox.Show(notepad, "If this is open when the program ends\nit will not lock up notepad...", "Message", MessageBoxButtons.OK, MessageBoxIcon.Information, MessageBoxDefaultButton.Button1, MessageBoxOptions.ServiceNotification);\n    });\n    thread.IsBackground = true;\n    thread.Start();\n}