private static void AddCharacterRange(List<char> list, char start, char end)\n{\n    for (char c = start; c <= end; c++)\n    {\n        list.Add(c);\n    }\n}\n\n// Usage:\nList<char> unallowed = new List<char>();\n\nAddCharacterRange(unallowed, '\u0000', '\u0008');\nAddCharacterRange(unallowed, '\u000B', '\u000C');\n// Add more ranges as needed...