using Oracle.ManagedDataAccess.Client;\n\n// Create a new OracleConnection instance\nOracleConnection connection = new OracleConnection();\n\n// Set the connection string to use Windows Authentication\nconnection.ConnectionString = "Data Source=(DESCRIPTION=(ADDRESS=(PROTOCOL=TCP)(HOST=your_host)(PORT=your_port))(CONNECT_DATA=(SERVICE_NAME=your_service_name)));User Id=/;Authentication=Windows";\n\n// Open the connection\nconnection.Open();