public void GrantReadAccess(string backupFile)\n{\n    string serviceName = "MSSQLSERVER"; // Default service name for SQL Server\n    string accessControl = $"sc sdshow {serviceName}";\n    string output = RunCommand(accessControl);\n\n    // Parse the output to extract the service SID\n    string serviceSid = ExtractServiceSid(output);\n\n    if (!string.IsNullOrEmpty(serviceSid))\n    {\n        string icaclsCommand = $"icacls {backupFile} /grant:r {serviceSid}:(R)";\n        RunCommand(icaclsCommand);\n    }\n}\n\nprivate string RunCommand(string command)\n{\n    Process process = new Process();\n    process.StartInfo.FileName = "cmd.exe";\n    process.StartInfo.Arguments = $"/c {command}";\n    process.StartInfo.UseShellExecute = false;\n    process.StartInfo.RedirectStandardOutput = true;\n    process.Start();\n\n    string output = process.StandardOutput.ReadToEnd();\n    process.WaitForExit();\n\n    return output;\n}\n\nprivate string ExtractServiceSid(string output)\n{\n    // Implement your own logic to extract the service SID from the output\n    // This may involve parsing the SDDL string and extracting the SID\n}