using Microsoft.SqlServer.Management.Common;\nusing Microsoft.SqlServer.Management.Smo;\n\n// Source and target database connections\nstring sourceConnStr = "Data Source=source_server;Initial Catalog=source_database;Integrated Security=True";\nstring targetConnStr = "Data Source=target_server;Initial Catalog=target_database;Integrated Security=True";\n\n// Create source and target database objects\nServer sourceServer = new Server(new ServerConnection(sourceConnStr));\nDatabase sourceDatabase = sourceServer.Databases["source_database"];\n\nServer targetServer = new Server(new ServerConnection(targetConnStr));\nDatabase targetDatabase = targetServer.Databases["target_database"];\n\n// Create a transfer object\nTransfer transfer = new Transfer(sourceDatabase);\n\n// Set transfer options (e.g., copy schema, data, or both)\ntransfer.CopySchema = true;\ntransfer.CopyData = true;\n\n// Transfer the database\ntransfer.TransferData(targetDatabase);