using System.DirectoryServices;\n\n// Create a new DirectoryEntry for the IIS metabase\nDirectoryEntry iis = new DirectoryEntry("IIS://localhost/W3SVC");\n\n// Get the website or application pool you want to control\nDirectoryEntry site = iis.Children.Find("1"); // Replace with the ID of the website or app pool\n\n// Start/stop the website or app pool\nsite.Invoke("Start");\n// or\nsite.Invoke("Stop");