using iTextSharp.text.pdf;\nusing System.Drawing;\n\n// Assuming you have a PDF file stored on the server\nstring pdfFilePath = @"C:\path\to\document.pdf";\n\n// Create a thumbnail image from the first page of the PDF\nusing (var pdfReader = new PdfReader(pdfFilePath))\n{\n    var page = pdfReader.GetPageN(1);\n    var thumbnail = page.GetThumbnailImage(120, 120); // adjust size as needed\n\n    // Save the thumbnail to a temporary location\n    string thumbnailPath = @"C:\temp\thumbnail.png";\n    thumbnail.Save(thumbnailPath, ImageFormat.Png);\n}