protected void GridView1_SelectedIndexChanged(object sender, EventArgs e)\n{\n    GridViewRow row = GridView1.SelectedRow;\n    Label1.Text = row.Cells[1].Text;\n\n    OleDbConnection myConnection = new OleDbConnection("Provider=Microsoft.Jet.OLEDB.4.0;Data Source=" + dbpath + "/secure_user/data/data.mdb");\n    OleDbCommand command = new OleDbCommand("SELECT tasks.Task,tasks.Priority,tasks.Done,taska.Hours FROM projects,tasks WHERE tasks.Username=@uname AND tasks.Project=@project", myConnection);\n    command.Parameters.AddWithValue("@uname", uname);\n    command.Parameters.AddWithValue("@project", Label1.Text);\n\n    OleDbDataAdapter adapter = new OleDbDataAdapter(command);\n    DataTable table = new DataTable();\n    adapter.Fill(table);\n    adapter.Dispose();\n\n    GridView2.DataSource = null; // Clear existing data\n    GridView2.DataSource = table;\n    GridView2.DataBind();\n    GridView2.Visible = true;\n}