using System.Management;\n\n// Create a WMI scope\nManagementScope scope = new ManagementScope("\\\\localhost\\ROOT\\WebAdministration");\n\n// Create a WMI query\nObjectQuery query = new ObjectQuery("SELECT * FROM Site");\n\n// Execute the query\nManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\nManagementObjectCollection sites = searcher.Get();\n\n// Iterate through the sites\nforeach (ManagementObject site in sites)\n{\n    // Configure the site\n    site["Name"] = "MySite";\n    site["PhysicalPath"] = "C:\\inetpub\\wwwroot";\n\n    // Save the changes\n    site.Put();\n}