var dataSet = new DataSet(); // assume this is your filled dataset\nvar tableName = "YourTableName"; // specify the table name\nvar columnName = "YourColumnName"; // specify the column name\n\nvar columnData = dataSet.Tables[tableName].AsEnumerable()\n    .Select(row => row.Field<string>(columnName))\n    .ToList();\n\n// bind the data to the ComboBox\ncomboBox.ItemsSource = columnData;