public struct LSN\n{\n    public uint VlfSequenceNumber;\n    public uint LogBlockNumber;\n    public ushort LogRecordNumber;\n\n    public byte[] ToBytes()\n    {\n        var bytes = new byte[10];\n        BitConverter.GetBytes(VlfSequenceNumber).CopyTo(bytes, 0);\n        BitConverter.GetBytes(LogBlockNumber).CopyTo(bytes, 4);\n        BitConverter.GetBytes(LogRecordNumber).CopyTo(bytes, 8);\n        return bytes;\n    }\n}