using System.Text.RegularExpressions;\n\nstring sqlPattern = @"\n    ^\n    (?:\n        (?:SELECT|INSERT|UPDATE)\s+  # match keyword\n        (?:.*?(?:FROM|INTO|SET)\s+)?  # match from/into/set clause\n        (?:\(?\w+\)?\s*,?\s*)+  # match columns/fields\n        (?:WHERE\s+.*?\s*)?  # match where clause\n        (?:GROUP BY\s+.*?\s*)?  # match group by clause\n        (?:ORDER BY\s+.*?\s*)?  # match order by clause\n    )\n    $\n";\n\nRegex regex = new Regex(sqlPattern, RegexOptions.IgnoreCase | RegexOptions.Multiline);