public void AddIssueLink(string fromIssueKey, string toIssueKey)\n{\n    JiraSoapServiceService jiraService = new JiraSoapServiceService();\n    string token = jiraService.login("username", "password");\n\n    RemoteIssue fromIssue = jiraService.getIssue(token, fromIssueKey);\n    RemoteIssue toIssue = jiraService.getIssue(token, toIssueKey);\n\n    RemoteIssueLink link = new RemoteIssueLink();\n    link.issueId1 = fromIssue.id;\n    link.issueId2 = toIssue.id;\n    link.typeId = 10000; // 10000 is the ID for the "Related Issues" link type\n\n    jiraService.addIssueLink(token, link);\n}