result = System.Text.RegularExpressions.Regex.Replace(result, \n    @"<(td|p|div)\b(?:[^>""']|""[^""]*""|'[^']*')*>", \n    match => {\n        if (match.Groups[1].Value == "td")\n            return "\t";\n        else\n            return match.Value.TrimStart('<').TrimEnd('>').ToLower() == "p" || \n                   match.Value.TrimStart('<').TrimEnd('>').ToLower() == "div" ? "\r\r" : "";\n    }, \n    System.Text.RegularExpressions.RegexOptions.IgnoreCase);