// Generate token\nstring token = FormsAuthentication.GetAuthCookie(User.Identity.Name, false).Value;\n\n// Pass token in hidden form field\n<input type="hidden" name="uploadToken" value="<%= token %>">\n\n// Validate token on server-side\nstring token = Request.Form["uploadToken"];\nif (FormsAuthentication.Decrypt(token).Name == User.Identity.Name)\n{\n    // Token is valid, proceed with upload\n}\nelse\n{\n    // Token is invalid, reject upload\n}