using System;\nusing System.Xml;\nusing com.atlassian.jira.rpc.soap.client;\n\nclass JiraSoapClient\n{\n    public void AddSubtask(string parentIssueKey, string childIssueKey)\n    {\n        JiraSoapServiceService jiraService = new JiraSoapServiceService();\n        string token = jiraService.login("username", "password");\n\n        RemoteIssue parentIssue = jiraService.getIssue(token, parentIssueKey);\n        RemoteIssue childIssue = jiraService.getIssue(token, childIssueKey);\n\n        RemoteSubtask subtask = new RemoteSubtask();\n        subtask.issueId = childIssue.id;\n        subtask.sequence = 1;\n\n        jiraService.addSubtask(token, parentIssue.id, subtask);\n    }\n}