using System.Data.Odbc;\n\n// Enumerate the available ODBC drivers\nDataTable drivers = OdbcConnection.GetSchema(OdbcMetaDataCollectionNames.Drivers);\n\n// Find the 32-bit ODBC driver\nDataRow[] results = drivers.Select("Driver_Name = 'your_driver_name' AND Driver_Bits = 32");\n\n// Create an OdbcConnection object using the 32-bit driver\nif (results.Length > 0)\n{\n    string connectionString = "DSN=your_dsn_name;Driver={" + results[0]["Driver_Name"] + "};";\n    using (OdbcConnection connection = new OdbcConnection(connectionString))\n    {\n        // Open the connection\n        connection.Open();\n\n        // Use the connection to execute queries or perform other database operations\n    }\n}