using System.Security;\nusing System.Security.Policy;\n\n// Create a new policy level\nPolicyLevel policyLevel = PolicyLevel.CreateAppDomainLevel();\n\n// Add a new code group\nCodeGroup codeGroup = new UnionCodeGroup(\n    new StrongNameMembershipCondition(typeof(MyAssembly).Assembly.Evidence),\n    new PermissionSet(PermissionState.Unrestricted)\n);\npolicyLevel.RootCodeGroup.AddChild(codeGroup);\n\n// Configure the policy level\nPolicyManager.AddPolicyLevel(policyLevel);