using System.Data;\nusing System.Data.SqlClient;\n\n// Create a connection to the SQL Server database\nstring connectionString = "Server=myServer;Database=myDatabase;Integrated Security=True";\nSqlConnection connection = new SqlConnection(connectionString);\n\n// Create a SqlDataAdapter instance\nSqlDataAdapter adapter = new SqlDataAdapter();\n\n// Create a DataSet to hold the schema\nDataSet dataSet = new DataSet();\n\n// Get the schema of the tables from SQL Server\nadapter.FillSchema(dataSet, SchemaType.Source, "Table1", "Table2", ...);\n\n// Load the schema into the DataSet\ndataSet.EnforceConstraints = true;\n\n// Now you can use the DataSet to generate the data and then use SqlBulkCopy to transfer it to SQL Server