public class Sizer\n{\n    private bool _hasInputImage;\n    private bool _hasOutputLocation;\n\n    public Sizer FromImage(string inputImage)\n    {\n        // ...\n        _hasInputImage = true;\n        return this;\n    }\n\n    public Sizer ToLocation(string outputLocation)\n    {\n        // ...\n        _hasOutputLocation = true;\n        return this;\n    }\n\n    public void Save()\n    {\n        if (!_hasInputImage || !_hasOutputLocation)\n        {\n            throw new InvalidOperationException("Missing required configuration");\n        }\n        // ...\n    }\n}