using System;\nusing System.Windows.Forms;\nusing FastColoredTextBoxNS;\n\npublic class CodeEditor : Form\n{\n    private FastColoredTextBox fctb;\n\n    public CodeEditor()\n    {\n        fctb = new FastColoredTextBox();\n        fctb.Dock = DockStyle.Fill;\n        fctb.Language = Language.CSharp; // Set language to C#\n        fctb.AutoCompleteBrackets = true;\n        fctb.AutoCompleteBracketsList = new char[] { '(', ')', '[', ']', '{', '}' };\n\n        this.Controls.Add(fctb);\n    }\n}