public class Producer\n{\n    private readonly SynchronizedQueue<int> _queue;\n\n    public Producer(SynchronizedQueue<int> queue)\n    {\n        _queue = queue;\n    }\n\n    public void Produce(int item)\n    {\n        _queue.Enqueue(item);\n    }\n}\n\npublic class Consumer\n{\n    private readonly SynchronizedQueue<int> _queue;\n\n    public Consumer(SynchronizedQueue<int> queue)\n    {\n        _queue = queue;\n    }\n\n    public int Consume()\n    {\n        return _queue.Dequeue();\n    }\n}