var categories = dt.AsEnumerable()\n    .Select(row => row.Field<int>("CategoryID"))\n    .Distinct();\n\nforeach (var category in categories)\n{\n    var last5News = dt.AsEnumerable()\n        .Where(row => row.Field<int>("CategoryID") == category)\n        .OrderByDescending(row => row.Field<DateTime>("DateAdded"))\n        .Take(5)\n        .Select(row => new {\n            NewsID = row.Field<int>("NewsID"),\n            NewsTitle = row.Field<string>("NewsTitle"),\n            NewsText = row.Field<string>("NewsText")\n        });\n    // Process the last 5 news items for this category\n}