protected void TreeView1_SelectedNodeChanged(object sender, EventArgs e)\n{\n    // Perform database transactions and retrieve the value\n    string retrievedValue = GetValueFromDatabase();\n\n    // Register a JavaScript function call\n    string jsFunctionCall = "passDataToParentPage('" + retrievedValue + "');";\n    ClientScript.RegisterStartupScript(this.GetType(), "TreeView_SelectionChanged", jsFunctionCall, true);\n}