@using Syncfusion.EJ2.Grid\n\n@Html.EJS().Grid("Grid").DataSource(data => data.Url("/api/data").Adaptor("UrlAdaptor")).Columns(col =>\n{\n    col.Field("OrderID").HeaderText("Order ID").IsPrimaryKey(true).Add();\n    col.Field("CustomerID").HeaderText("Customer ID").Add();\n    col.Field("Freight").HeaderText("Freight").Add();\n}).EditSettings(edit => { edit.AllowAdding(true).AllowDeleting(true).AllowEditing(true).Mode(Syncfusion.EJ2.Grid.EditMode.Batch); }).AllowPaging().Render()