using System;\nusing System.Web;\nusing Google.ReCaptcha;\n\npublic partial class CaptchaPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        var recaptcha = new ReCaptcha();\n        recaptcha.SiteKey = "YOUR_SITE_KEY";\n        recaptcha.SecretKey = "YOUR_SECRET_KEY";\n\n        if (recaptcha.Verify(Request.Form["g-recaptcha-response"]))\n        {\n            // CAPTCHA validation successful\n        }\n        else\n        {\n            // CAPTCHA validation failed\n        }\n    }\n}