public class WebAgentPermission\n{\n    private BitArray permissions;\n\n    public WebAgentPermission()\n    {\n        permissions = new BitArray(64); // initialize with 64 bits\n    }\n\n    public void SetPermission(int index, bool value)\n    {\n        if (index < 0 || index >= permissions.Length)\n        {\n            throw new IndexOutOfRangeException("Index out of range");\n        }\n        permissions[index] = value;\n    }\n\n    public bool GetPermission(int index)\n    {\n        if (index < 0 || index >= permissions.Length)\n        {\n            throw new IndexOutOfRangeException("Index out of range");\n        }\n        return permissions[index];\n    }\n}