using ESRI.ArcGIS.Framework;\nusing ESRI.ArcGIS.esriSystem;\n\n// ...\n\nprivate void FindCommands()\n{\n    // Get the ArcMap application\n    IApplication app = (IApplication) Marshal.GetActiveObject("ArcMap.Application");\n\n    // Get the loaded assemblies\n    IArray assemblies = app.GetLoadedAssemblies();\n\n    // Iterate over the assemblies\n    for (int i = 0; i < assemblies.Count; i++)\n    {\n        Assembly assembly = (Assembly) assemblies.get_Element(i);\n        Type[] types = assembly.GetTypes();\n        foreach (Type type in types)\n        {\n            if (typeof(ICommand).IsAssignableFrom(type))\n            {\n                // Found a command type\n                Console.WriteLine(type.FullName);\n            }\n        }\n    }\n}