using SharpUSBLib;\n\nclass UsbPrinter\n{\n    public void Print(string printerName, byte[] data)\n    {\n        USBDevice device = new USBDevice(0x03EB, 0x6124); // replace with your printer's VID and PID\n        if (device != null)\n        {\n            device.Write(data);\n            device.Close();\n        }\n        else\n        {\n            Console.WriteLine("Error opening printer");\n        }\n    }\n}