using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.Xml;\nusing System.Xml;\n\npublic class XmlSigner\n{\n    public static string SignXml(string xmlString, string privateKeyPath)\n    {\n        // Load the XML document\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlString);\n\n        // Load the private key\n        using (var privateKey = new RSACryptoServiceProvider())\n        {\n            privateKey.FromXmlString(File.ReadAllText(privateKeyPath));\n\n            // Create a SignedXml object\n            var signedXml = new SignedXml(xmlDoc);\n            signedXml.SigningKey = privateKey;\n\n            // Add a Reference to the XML document\n            var reference = new Reference();\n            reference.Uri = "";\n            signedXml.AddReference(reference);\n\n            // Add a digital signature\n            signedXml.ComputeSignature();\n\n            // Get the signed XML\n            var signedXmlString = xmlDoc.OuterXml;\n\n            return signedXmlString;\n        }\n    }\n}