using System;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Console.CancelKeyPress += (sender, e) =>\n        {\n            Console.WriteLine("Cancel key pressed. Shutting down...");\n            Environment.Exit(0);\n        };\n\n        while (true)\n        {\n            // Do nothing, just keep the console open\n            Thread.Sleep(100);\n        }\n    }\n}