using NAudio.Wave;\nusing NAudio.Flac;\n\n// Load the WAV file\nWaveFileReader wavReader = new WaveFileReader("input.wav");\n\n// Create a FLAC writer\nFlacWriter flacWriter = new FlacWriter("output.flac", wavReader.WaveFormat);\n\n// Copy the audio data from the WAV reader to the FLAC writer\nbyte[] buffer = new byte[4096];\nint bytesRead;\nwhile ((bytesRead = wavReader.Read(buffer, 0, buffer.Length)) > 0)\n{\n    flacWriter.Write(buffer, 0, bytesRead);\n}\n\n// Close the writers\nwavReader.Close();\nflacWriter.Close();