using NAudio.Wave;\nusing NAudio.Opus;\n\n// Load the WAV file\nWaveFileReader wavReader = new WaveFileReader("input.wav");\n\n// Create an Opus encoder\nOpusEncoder opusEncoder = new OpusEncoder(wavReader.WaveFormat, 192000);\n\n// Create a byte array to hold the encoded audio\nbyte[] encodedAudio = new byte[wavReader.Length];\n\n// Encode the audio\nint encodedBytes = opusEncoder.Encode(wavReader, encodedAudio);\n\n// Save the encoded audio to a file\nFile.WriteAllBytes("output.opus", encodedAudio.Take(encodedBytes).ToArray());