// In your web application\npublic class ConnectionStringProvider\n{\n    public static string GetConnectionString()\n    {\n        var connectionStringManager = new ConnectionStringManager();\n        return connectionStringManager.ConnectionStrings["YourConnectionStringName"].ConnectionString;\n    }\n}\n\n// In your DLL\npublic class YourDllClass\n{\n    public void YourMethod(string connectionString)\n    {\n        // Use the connection string here\n    }\n}\n\n// In your web application, when calling the DLL method\nvar connectionString = ConnectionStringProvider.GetConnectionString();\nvar dll = new YourDllClass();\ndll.YourMethod(connectionString);