using System;\nusing System.Diagnostics.Tracing;\n\nclass AllocationTracker : EventListener\n{\n    protected override void OnEventSourceCreated(EventSource eventSource)\n    {\n        if (eventSource.Name == "System.GC")\n        {\n            eventSource.IsEnabled = true;\n        }\n    }\n\n    protected override void OnEventWritten(EventWrittenEventArgs eventData)\n    {\n        if (eventData.EventId == 1) // GCHeapStats\n        {\n            Console.WriteLine($"GC Heap Size: {eventData.Payload[0]}");\n            Console.WriteLine($"Large Object Heap Size: {eventData.Payload[1]}");\n            Console.WriteLine($"Pinned Object Heap Size: {eventData.Payload[2]}");\n            Console.WriteLine($"Finalization Queue Length: {eventData.Payload[3]}");\n        }\n        else if (eventData.EventId == 2) // GCTime\n        {\n            Console.WriteLine($"GC Time: {eventData.Payload[0]}");\n        }\n    }\n}