public static bool VerifySignature(string signedXmlString, string publicKeyPath)\n{\n    // Load the signed XML document\n    XmlDocument xmlDoc = new XmlDocument();\n    xmlDoc.LoadXml(signedXmlString);\n\n    // Load the public key\n    using (var publicKey = new RSACryptoServiceProvider())\n    {\n        publicKey.FromXmlString(File.ReadAllText(publicKeyPath));\n\n        // Create a SignedXml object\n        var signedXml = new SignedXml(xmlDoc);\n        signedXml.SigningKey = publicKey;\n\n        // Check the signature\n        return signedXml.CheckSignature();\n    }\n}