using System.Data.SqlClient;\n\n// assume RecordIDList is a List<string>\nstring connectionString = "your_connection_string";\nstring query = "SELECT * FROM Table WHERE RecordID IN (@RecordIDList)";\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    SqlCommand command = new SqlCommand(query, connection);\n    command.Parameters.AddWithValue("@RecordIDList", string.Join(",", RecordIDList));\n\n    // execute the query\n    SqlDataReader reader = command.ExecuteReader();\n    while (reader.Read())\n    {\n        // process the results\n    }\n}