using Devart.Data.Oracle;\n\n// Create a connection string\nstring connectionString = "Server=myServer;User Id=myUser;Password=myPassword;";\n\n// Create a connection object\nOracleConnection connection = new OracleConnection(connectionString);\n\n// Open the connection\nconnection.Open();\n\n// Create a command object\nOracleCommand command = new OracleCommand("SELECT myGeometry FROM myTable", connection);\n\n// Execute the command and retrieve the geometry object\nOracleDataReader reader = command.ExecuteReader();\nwhile (reader.Read())\n{\n    OracleObject myGeometry = reader.GetOracleObject(0);\n    // Process the geometry object\n}\n\n// Close the reader and connection\nreader.Close();\nconnection.Close();