using System;\nusing System.Data.SqlClient;\n\npublic class SqlServerStatusChecker\n{\n    public bool CheckSqlServerStatus(string connectionString)\n    {\n        try\n        {\n            using (var connection = new SqlConnection(connectionString))\n            {\n                connection.Open();\n                using (var command = new SqlCommand("SELECT 1", connection))\n                {\n                    command.ExecuteNonQuery();\n                }\n            }\n            return true;\n        }\n        catch (SqlException ex)\n        {\n            Console.WriteLine($"Error checking SQL server: {ex.Message}");\n            return false;\n        }\n    }\n}