using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class ServerStatusChecker\n{\n    private readonly HttpClient _httpClient;\n\n    public ServerStatusChecker()\n    {\n        _httpClient = new HttpClient();\n    }\n\n    public async Task CheckServerStatusAsync(string serverUrl)\n    {\n        try\n        {\n            var response = await _httpClient.GetAsync(serverUrl);\n            if (response.StatusCode >= 200 && response.StatusCode < 300)\n            {\n                Console.WriteLine($"Server {serverUrl} is online");\n            }\n            else\n            {\n                Console.WriteLine($"Server {serverUrl} is offline");\n                // Send alert email to network manager\n            }\n        }\n        catch (HttpRequestException ex)\n        {\n            Console.WriteLine($"Error checking server {serverUrl}: {ex.Message}");\n            // Send alert email to network manager\n        }\n    }\n}